include_once("connection.php");

function getPosts()
{
    $posts = array();
    $posts['id'] = $_POST['id'];
    $posts['fname'] = $_POST['fname'];
    $posts['lname'] = $_POST['lname'];
    $posts['age'] = $_POST['age'];
    $posts['birthday'] = $_POST['birthday'];
    $posts['address'] = $_POST['address'];
    $posts['contact'] = $_POST['contact'];
    $posts['nationality'] = $_POST['nationality'];
    $posts['status'] = $_POST['status'];
    $posts['gender'] = $_POST['gender'];
    return $posts;
}






if (isset($_POST['insert'])) {
    // Ensure that the $_POST keys match your form input names
    $data = array(
        'id' => $_POST['id'],
        'fname' => $_POST['fname'],
        'lname' => $_POST['lname'],
        'age' => $_POST['age'],
        'birthday' => $_POST['birthday'],
        'address' => $_POST['address'],
        'contact' => $_POST['contact'],
        'nationality' => $_POST['nationality'],
        'status' => $_POST['status'],
        'gender' => $_POST['gender']
    );

    $insert_Query = "INSERT INTO `users` (`id`, `fname`, `lname`, `age`, `birthday`, `address`, `contact`, `nationality`, `status`, `gender`) VALUES (
        '{$data['id']}', '{$data['fname']}', '{$data['lname']}', '{$data['age']}', '{$data['birthday']}', '{$data['address']}', '{$data['contact']}', '{$data['nationality']}', '{$data['status']}', '{$data['gender']}'
    )";

    try {
        $insert_Result = mysqli_query($connect, $insert_Query);

        if ($insert_Result) {
            if (mysqli_affected_rows($connect) > 0) {
                echo 'Data Inserted';
            } else {
                echo 'No Data Inserted';
            }
        }
    } catch (Exception $ex) {
        echo 'Error Insert' . $ex->getMessage();
    }
}


if (isset($_POST['delete'])) {
    $data = getPosts();
    try {
        // Check if the connection is established
        if ($connect) {
            $delete_Query = "DELETE FROM `users` WHERE `id` = '{$data['id']}'";
            
            $delete_Result = mysqli_query($connect, $delete_Query);

            if ($delete_Result) {
                if (mysqli_affected_rows($connect) > 0) {
                    echo 'Data Deleted';
                } else {
                    echo 'No Data Deleted';
                }
            } else {
                echo 'Query execution failed';
            }
        } else {
            echo 'Database connection failed';
        }
    } catch (Exception $ex) {
        echo 'Error Delete: ' . $ex->getMessage();
    }
}

if (isset($_POST['search'])) {
    $data = getPosts();
    $search_Query = "SELECT * FROM `users` WHERE `id` = '{$data['id']}'";
    
    $search_Result = mysqli_query($connect, $search_Query);
    
    if ($search_Result) {
        if (mysqli_num_rows($search_Result) > 0) {
            $row = mysqli_fetch_assoc($search_Result);
            $id = $row['id'];
            $fname = $row['fname'];
            $lname = $row['lname'];
            $age = $row['age'];
            $birthday = $row['birthday'];
            $address = $row['address'];
            $contact = $row['contact'];
            $nationality = $row['nationality'];
            $status = $row['status'];
            $gender = $row['gender'];
        } else {
            echo 'No Data for this ID';
        }
    } else {
        echo 'Result Error';
    }
}

if (isset($_POST['update'])) {
    $data = getPosts();
    $update_Query = "UPDATE `users` SET
        `fname` = '{$data['fname']}',
        `lname` = '{$data['lname']}',
        `age` = '{$data['age']}',
        `birthday` = '{$data['birthday']}',
        `address` = '{$data['address']}',
        `contact` = '{$data['contact']}',
        `nationality` = '{$data['nationality']}',
        `status` = '{$data['status']}',
        `gender` = '{$data['gender']}'
        WHERE `id` = '{$data['id']}'";
    try {
        $update_Result = mysqli_query($connect, $update_Query);

        if ($update_Result) {
            if (mysqli_affected_rows($connect) > 0) {
                echo 'Data Updated';
            } else {
                echo 'No Data Updated';
            }
        }
    } catch (Exception $ex) {
        echo 'Error Update: ' . $ex->getMessage();
    }
}



